import {
  Button,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  CardActionArea,
} from "@mui/material";
// import Progress from './Progress';
import { FC } from "react";
import { IProduct } from "../screens/Home";

const ProductCard: FC<IProduct> = ({
  name,
  img,
  description,
  price,
  rating,
}) => {
  function formatPrice(price: number) {
    return (price / 100).toFixed(2) + " USD";
  }
  return (
    <Card sx={{ maxWidth: { xs: 300, md: 345 }, minWidth: { xs: 300, md: 345 }, height: "400px" }}>
      <CardActionArea>
        <CardMedia component="img" alt={name} height={150} width={150} image={img} />
        <CardContent>
          <Typography
            gutterBottom
            fontWeight="bold"
            variant="subtitle2"
            component="div"
            sx={{ height: "40px" }}
          >
            {name}
          </Typography>
          {/* <Typography
            variant="body2"
            color="text.primary"
            paragraph={true}
            fontSize="1 rem"
            sx={{
              height: "80px",
              overflow: "hidden",
              textOverflow: "ellipsis",
            }}
          >
            {description}
          </Typography> */}
          <Typography
            variant="body1"
            fontWeight="bold"
            fontSize="1 rem"
            color="#222"
          >
            {formatPrice(price)}
          </Typography>
        </CardContent>
        <CardActions>
          <Button type="submit">Add to order</Button>
          {/* <Progress currentAmount={currentAmount} goalAmount={goalAmount} contributions={contributions} /> */}
        </CardActions>
      </CardActionArea>
    </Card>
  );
};

export default ProductCard;
