import {
  Button,
  ButtonBase,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  CardActionArea,
} from "@mui/material";
import { styled } from '@mui/material/styles';
import { FC } from "react";
import { IProduct } from "../screens/Home";

const ProductCards: FC<IProduct> = ({
  name,
  img,
  description,
  price,
  rating,
}) => {
  function formatPrice(price: number) {
    return "$" + (price / 100).toFixed(2) + " USD";
  }

  const Img = styled('img')({
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '80%',
  });

  return (
    <Card sx={{ maxWidth: { xs: 300, md: 345 }, minWidth: { xs: 300, md: 345 }, height: "450px" }}>
      <CardActionArea>
      <CardMedia sx={{ width: "100%", height: 260 }}>
      <Img alt={name} src={img} />
          </CardMedia>
        <CardContent>
          <Typography
            gutterBottom
            fontWeight="bold"
            variant="subtitle2"
            component="div"
            sx={{ height: "80px" }}
          >
            {name}
          </Typography>
          {/* <Typography
            variant="body2"
            color="text.primary"
            paragraph={true}
            fontSize="1 rem"
            sx={{
              height: "70px",
              overflow: "hidden",
              textOverflow: "ellipsis",
            }}
          >
            {description}
          </Typography> */}
          <Typography
            variant="body1"
            fontWeight="bold"
            fontSize="1 rem"
            color="#222"
          >
            {formatPrice(price)}
          </Typography>
        </CardContent>
        <CardActions sx={{justifyContent: 'flex-end'}}>
          <Button type="submit">Add to order</Button>
        </CardActions>
      </CardActionArea>
    </Card>
  );
};

export default ProductCards;
