import { useState } from 'react';
import { useQuery } from 'react-query';
// Components
import Products from '<div className=""></div>/Products/products';
import Cart from './Cart/Cart';
import Drawer from '@material-ui/core/Drawer';
import LinearProgress from '@material-ui/core/LinearProgress';
import Grid from '@material-ui/core/Grid';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import Badge from '@material-ui/core/Badge';
// Styles
import { Wrapper, StyledButton } from './App.styles';
// Types
export type CartProductType = {
  id: number;
  name: string;
  description: string;
  category: string;
  img: string;
  price: number;
  quantity: number;
  countInStock: number;
  rating: number
};

const getProducts = async (): Promise<CartProductType[]> =>
  await (await fetch('http://localhost:5000/products')).json();

const App = () => {
  const [cartOpen, setCartOpen] = useState(false);
  const [cartproducts, setCartproducts] = useState([] as CartProductType[]);
  const { data, isLoading, error } = useQuery<CartProductType[]>(
    'products',
    getProducts
  );
  console.log(data);

  const getTotalproducts = (products: CartProductType[]) =>
    products.reduce((ack: number, product) => ack + product.quantity, 0);

  const handleAddToCart = (clickedproduct: CartProductType) => {
    setCartproducts(prev => {
      // 1. Is the product already added in the cart?
      const isproductInCart = prev.find(product => product.id === clickedproduct.id);

      if (isproductInCart) {
        return prev.map(product =>
          product.id === clickedproduct.id
            ? { ...product, quantity: product.quantity + 1 }
            : product
        );
      }
      // First time the product is added
      return [...prev, { ...clickedproduct, quantity: 1 }];
    });
  };

  const handleRemoveFromCart = (id: number) => {
    setCartproducts(prev =>
      prev.reduce((ack, product) => {
        if (product.id === id) {
          if (product.quantity === 1) return ack;
          return [...ack, { ...product, quantity: product.quantity - 1 }];
        } else {
          return [...ack, product];
        }
      }, [] as CartProductType[])
    );
  };

  if (isLoading) return <LinearProgress />;
  if (error) return <div>Something went wrong ...</div>;

  return (
    <Wrapper>
      <Drawer anchor='right' open={cartOpen} onClose={() => setCartOpen(false)}>
        <Cart
          cartproducts={cartproducts}
          addToCart={handleAddToCart}
          removeFromCart={handleRemoveFromCart}
        />
      </Drawer>
      <StyledButton onClick={() => setCartOpen(true)}>
        <Badge badgeContent={getTotalproducts(cartproducts)} color='error'>
          <AddShoppingCartIcon />
        </Badge>
      </StyledButton>
      <Grid container spacing={3}>
        {data?.map(product => (
          <Grid product key={product.id} xs={12} sm={4}>
            <product product={product} handleAddToCart={handleAddToCart} />
          </Grid>
        ))}
      </Grid>
    </Wrapper>
  );
};

export default App;
