import { Card, CardMedia, CardContent, Typography, CardActions, CardActionArea } from '@mui/material';
// import Progress from './Progress';
import { FC } from 'react';
import { IProduct } from '../screens/Home';

const ProductCard: FC<IProduct> = ({ name, img, description, price, rating }) => {
   function formatPrice(price: number) {
    return (price / 100).toFixed(2).replace('.', ',') + ' SEK';
  }
  return (
    <Card sx={{ maxWidth: { xs: 500, md: 345 }, height: '460px' }}>
      <CardActionArea>
        <CardMedia component="img" alt={name}
          height={160} image={img}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div" sx={{ height: '95px' }}>
            {name}
          </Typography>
          <Typography
            variant="body2"
            color="text.primary"
            paragraph={true}
            sx={{ height: '60px', overflow: 'hidden', textOverflow: 'ellipsis' }}>
            {description}
          </Typography>
          <Typography
              variant="h2"
              fontWeight="bold"
              fontSize="1.5rem"
              color="#222">
              ${price} USD
            </Typography>
        </CardContent>
        <CardActions>
          {/* <Progress currentAmount={currentAmount} goalAmount={goalAmount} contributions={contributions} /> */}
        </CardActions>
      </CardActionArea>
    </Card>
  );
};

export default ProductCard;
